import Loader from './generic/Loader.vue';
import axios from 'axios';
export default {
  name: 'Home',
  components: {
    loader: Loader
  },
  data() {
    return {
      loading: false,
      mainImage: null
    };
  },
  computed: {
    computedHeight() {
      return window.innerHeight * 0.9;
    }
  },
  beforeMount() {
    window.setTimeout(() => this.getMainPhoto(), 500);
  },
  methods: {
    async getMainPhoto() {
      this.loading = true;
      let response;
      const request = axios.get(`${window.location.origin}/public-images/modern-min.png`, {
        responseType: 'arraybuffer'
      });
      try {
        response = await request;
        this.mainImage = Buffer.from(response.data, 'binary').toString('base64');
        this.loading = false;
      } catch (e) {
        console.error('There was an error', e);
        this.loading = false;
        return;
      }
      this.loading = false;
    }
  }
};
